                  $("#bairro").val(dados.bairro);                                $("#cidade").val(dados.localidade);                                $("#uf").val(dados.uf);                                $("#ibge").val(dados.ibge);                            } //end if.                            else {                                //CEP pesquisado não foi encontrado.                                limpa_formulário_cep();                                alert("CEP não encontrado.");                            }                        });                    } //end if.                    else {                        //cep é inválido.                        limpa_formulário_cep();                        alert("Formato de CEP inválido.");                    }                } //end if.                else {                    //cep sem valor, limpa formulário.                    limpa_formulário_cep();                }            });        });    </script></head><!-- END HEAD --><!-- BEGIN BODY --><body class="login-body">   <div id="container" class="form-vertical no-padding no-margin">      <div id="main-content">         <!-- BEGIN PAGE CONTAINER-->         <div class="container-fluid">            <!-- BEGIN PAGE HEADER-->               <div class="row-fluid">               <div class="span12">                   <!-- END THEME CUSTOMIZER-->                  <h3 class="page-title">                     Formul&aacute;rio de Cadastro                     <small>Cadastro de Pessoas</small>                  </h3>               </div>            </div>            <!-- END PAGE HEADER-->            <!-- BEGIN PAGE CONTENT-->            <div class="row-fluid">               <div class="span12">                  <div class="widget box blue" id="form_wizard_1">                     <div class="widget-title">                        <h4>                           <i class="icon-reorder"></i> Formul&aacute;rio de Cadastr