f ($maiusculas) $caracteres .= $lmai;		if ($numeros) $caracteres .= $num;		if ($simbolos) $caracteres .= $simb;		$len = strlen($caracteres);		for ($n = 1; $n <= $tamanho; $n++) {		$rand = mt_rand(1, $len);		$retorno .= $caracteres[$rand-1];		}		return $retorno;		}				//	$chave = geraSenha(40, true, false);	  $MM_restrictGoTo = $MM_restrictGoTo. $MM_qsChar . "accesscheck=" . $chave;  header("Location: ". $MM_restrictGoTo);   exit;}?><!DOCTYPE html><html xmlns="http://www.w3.org/1999/xhtml" lang="pt-br" xml:lang="pt-br"><head><meta http-equiv="Content-Type" content="text/html; charset=utf-8" />	<title> Sistema de Gest&atilde;o | Igreja de Deus</title>	<meta content="width=device-width, initial-scale=1.0" name="viewport" />	<meta content="" name="description" />	<meta content="" name="author" />	<link href="assets/bootstrap/css/bootstrap.min.css" rel="stylesheet" media="all" />	<link href="assets/bootstrap/css/bootstrap-responsive.min.css" rel="stylesheet" media="all" />   <link href="assets/bootstrap/css/bootstrap-fileupload.css" rel="stylesheet" />	<link href="assets/font-awesome/css/font-awesome.css" rel="stylesheet"  media="all"/>	<link href="css/style.css" rel="stylesheet" media="all" />	<link href="css/style_responsive.css" rel="stylesheet" media="all" />	<link href="css/style_default.css" rel="stylesheet" id="style_color"  media="all"/>	<link rel="stylesheet" type="text/css" href="assets/chosen-bootstrap/chosen/chosen.css"  media="all"/>	<link href="img/favicon.ico" rel="shortcut icon" type="image/x-icon">	<link href="assets/fancybox/source/jquery.fancybox.css" rel="stylesheet"  media="all"/>	<link rel="stylesheet" type="text/css" href="assets/uniform/css/uniform.default.css"  media="all" />   <link rel="stylesheet" type="text/css" href="assets/gritter/css/jquery.gritter.css" />   <link rel="stylesheet" type="text/css" href="assets/jquery-tags-input/jquery.tagsinput.c