<?phpinclude("conexao/conecta.php"); function geraSenha($tamanho = 8, $maiusculas = true, $numeros = true, $simbolos = false)		{		$lmin = 'abcdefghijklmnopqrstuvwxyz';		$lmai = 'ABCDEFGHIJKLMNOPQRSTUVWXYZ';		$num = '1234567890';		$simb = '!@#$%*-';		$retorno = '';		$caracteres = '';		$caracteres .= $lmin;		if ($maiusculas) $caracteres .= $lmai;		if ($numeros) $caracteres .= $num;		if ($simbolos) $caracteres .= $simb;		$len = strlen($caracteres);		for ($n = 1; $n <= $tamanho; $n++) {		$rand = mt_rand(1, $len);		$retorno .= $caracteres[$rand-1];		}		return $retorno;		}//pega a variavel via post$email = $_POST['email'];//busca no db o usuario com o email $result = mysqli_query($conecta,"SELECT * FROM pessoa WHERE email='$email'");$ver=mysqli_fetch_array($result);$id=$ver["id"];$nome=$ver["nome"];$funcao=$ver["funcao"];if($funcao=="Pastor"){     $resultpastor = mysqli_query($conecta,"SELECT * FROM pastor WHERE membro_id='id'");$verpastor=mysqli_fetch_array($resultpastor);$usuario_id=$verpastor["usuario_id"];$resultusuario = mysqli_query($conecta,"SELECT * FROM usuario WHERE id='$usuario_id'");$verusuario=mysqli_fetch_array($resultusuario);$login=$verusuario["login"];$senha=geraSenha(6, true, true);$password=md5($senha);$alterarsenha = mysqli_query($conecta,"UPDATE `usuario` SET `senha` = '".$password."' WHERE id= {$verusuario["id"]};");}$email=$ver["email"];                //enviar um email para variavel email juntamente com a variável senha;$mensage ="Ola ".$nome." você solicitou a recuperação de senhha confira seu dados.<br>";$mensage .="Login: " . $login. "<br>";$mensage .="Senha:" . $senha. "<br>";$mensage .="Mensagem enviada para o email: " . $email. "<br>";$mail=mail($email, "Recuperação de Senha", $mensage);if($mail){echo"<script>alert(Sua senha foi enviada para o e-mail indicado.),window.open('login.php','_self')</script>";}else{                echo"<script>alert('E-mail não cadastrado em nosso sistema'),window.open('login.php','_self')</script>";        }?>